import axios from 'axios';

const state = {
  users: [],
  selectedUser: null,
  loading: false,
  error: null,
};

const mutations = {
  SET_USERS(state, users) {
    state.users = users;
  },
  SET_SELECTED_USER(state, user) {
    state.selectedUser = user;
  },
  SET_LOADING(state, loading) {
    state.loading = loading;
  },
  SET_ERROR(state, error) {
    state.error = error;
  },
};

const actions = {
  async fetchUsers({ commit, rootState }) {
    commit('SET_LOADING', true);
    try {
      const token = rootState.token; 
      let header = { Authorization: "Bearer " + token };
      let configuration = { headers: header };
      const response = await axios.get('api/Users/ReadUsers', configuration);
      commit('SET_USERS', response.data);
    } catch (error) {
      commit('SET_ERROR', error.message);
    } finally {
      commit('SET_LOADING', false);
    }
  },
  async fetchUser({ commit, rootState }, text) {
    commit('SET_LOADING', true);
    try {
      const token = rootState.token; 
      let header = { Authorization: "Bearer " + token };
      let configuration = { headers: header };
      const response = await axios.get(`api/Users/ReadUser/${text}`, configuration);
      commit('SET_USERS', response.data);
    } catch (error) {
      commit('SET_ERROR', error.message);
    } finally {
      commit('SET_LOADING', false);
    }
  },
  async createUser({ dispatch, rootState }, userData) {
    const token = rootState.token; 
    let header = { Authorization: "Bearer " + token };
    let configuration = { headers: header };
      await axios.post('api/Users/CreateUser', userData, configuration);
      dispatch('fetchUsers');
 
  },
  async updateUser({ dispatch, rootState }, userData) {
    const token = rootState.token; 
    let header = { Authorization: "Bearer " + token };
    let configuration = { headers: header };
    await axios.put('api/Users/UpdateUser', userData, configuration);
    dispatch('fetchUsers');
  },
  async enabledUser({ dispatch, rootState }, id) {
    const token = rootState.token; 
    let header = { Authorization: "Bearer " + token };
    let configuration = { headers: header };
    await axios.put(`api/Users/EnabledUser/${id}`, {}, configuration);
    dispatch('fetchUsers');
  },
  async disabledUser({ dispatch, rootState }, id) {
    const token = rootState.token; 
    let header = { Authorization: "Bearer " + token };
    let configuration = { headers: header };
    await axios.put(`api/Users/DisabledUser/${id}`, {}, configuration);
    dispatch('fetchUsers');
  },
};

const getters = {
  users: (state) => state.users,
  selectedUser: (state) => state.selectedUser,
  loading: (state) => state.loading,
  error: (state) => state.error,
};

export default {
  namespaced: true,
  state,
  mutations,
  actions,
  getters,
};
