import axios from 'axios';

const state = {
  brands: [],
  brand: null,
};

const getters = {
  allBrands: (state) => state.brands,
  currentBrand: (state) => state.brand,
};

const actions = {
  async fetchBrands({ commit }) {
    try {
      const response = await axios.get('api/Brands/ReadBrands');
      commit('setBrands', response.data);
    } catch (error) {
      console.error('Error fetching brands:', error);
    }
  },
  async addBrand({ dispatch }, brand) {
    try {
      await axios.post('api/Brands/CreateBrand', brand);
      dispatch('fetchBrands');
    } catch (error) {
      console.error('Error adding brand:', error);
    }
  },
  async updateBrand({ dispatch }, brand) {
    try {
      await axios.put(`api/Brands/UpdateBrand/${brand.pK_BRAND}`, brand);
      dispatch('fetchBrands');
    } catch (error) {
      console.error('Error updating brand:', error);
    }
  },
/*   async deleteBrand({ dispatch }, brandId) {
    try {
      await axios.delete(`/brands/${brandId}`);
      dispatch('fetchBrands');
    } catch (error) {
      console.error('Error deleting brand:', error);
    }
  }, */
  setCurrentBrand({ commit }, brand) {
    commit('setCurrentBrand', brand);
  },
};

const mutations = {
  setBrands: (state, brands) => (state.brands = brands),
  setCurrentBrand: (state, brand) => (state.brand = brand),
};

export default {
  namespaced: true,
  state,
  getters,
  actions,
  mutations,
};

/* State:
brands: Un array que contiene todas las marcas obtenidas de la API.
selectedBrand: Un objeto que representa la marca actualmente seleccionada para edición.
loading: Un booleano que indica si se está cargando información (por ejemplo, al hacer una solicitud a la API).
