import { createRouter, createWebHistory } from 'vue-router'
import DashboardView from '@/views/DashboardView.vue'
import AboutView from '@/views/AboutView.vue'
import BrandsView from '@/views/BrandsView.vue'
import CategoriesView from '@/views/CategoriesView.vue'
import LoginView from '@/views/LoginView.vue'
import ProductsView from '@/views/ProductsView.vue'
import RolesView from '@/views/RolesView.vue'
import StoresView from '@/views/StoresView.vue'
import SuppliersView from '@/views/SuppliersView.vue'
import UsersView from '@/views/UsersView.vue'

const routes = [
  {
    path: '/',
    name: 'dashboard',
    component: DashboardView
  },
  {
    path: '/about',
    name: 'about',
    // route level code-splitting
    // this generates a separate chunk (about.[hash].js) for this route
    // which is lazy-loaded when the route is visited.
    //component: () => import(/* webpackChunkName: "about" */ '../views/AboutView.vue')
    component: AboutView
  },
  {
    path: '/brands',
    name: 'brands',
    component: BrandsView
  },
  {
    path: '/categories',
    name: 'categories',
    component: CategoriesView
  },
  {
    path: '/login',
    name: 'login',
    component: LoginView
  },
  {
    path: '/roles',
    name: 'roles',
    component: RolesView
  },
  {
    path: '/products',
    name: 'products',
    component: ProductsView
  },
  {
    path: '/stores',
    name: 'stores',
    component: StoresView
  },
  {
    path: '/suppliers',
    name: 'suppliers',
    component: SuppliersView
  },
  {
    path: '/users',
    name: 'users',
    component: UsersView
  },
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

router.beforeEach((to, from, next) => {
  if(to.matched.some(record => record.meta.libre)){
    next()
  } else if (store.state.user && store.state.user.role_name == 'ADMINISTRADORES'){
    if (to.matched.some(record => record.meta.administrator)){
        next()
    }
  } else if (store.state.usuario && store.state.usuario.rol == 'BODEGUEROS'){
    if (to.matched.some(record => record.meta.grocer)){
        next()
    }
  } else if (store.state.usuario && store.state.usuario.rol == 'OPERARIOS'){
    if (to.matched.some(record => record.meta.operator)){
        next()
    }
  } else {
    next({
      name: 'login'
    })
  }
})

export default router
