import { mapGetters, mapActions } from 'vuex';
import BrandForm from './BrandForm.vue';
export default {
  components: {
    BrandForm
  },
  data() {
    return {
      modal: false,
      selectedBrand: null
    };
  },
  computed: {
    ...mapGetters('brand', ['brands', 'loading']),
    headers() {
      return [{
        text: 'ID',
        value: 'pK_BRAND'
      }, {
        text: 'Nombre',
        value: 'branD_NAME'
      }, {
        text: 'Estado',
        value: 'status'
      }, {
        text: 'Acciones',
        value: 'actions',
        sortable: false
      }];
    }
  },
  methods: {
    ...mapActions('brand', ['fetchBrands', 'deleteBrand']),
    openModal() {
      this.selectedBrand = {
        pK_BRAND: null,
        branD_NAME: '',
        status: 'ACTIVO'
      };
      this.modal = true;
    },
    editBrand(brand) {
      this.selectedBrand = {
        ...brand
      }; // Clonamos el objeto para evitar mutaciones directas
      this.modal = true;
    },
    async deleteBrand(id) {
      const confirmDelete = confirm('¿Estás seguro de que deseas eliminar esta marca?');
      if (confirmDelete) {
        await this.deleteBrand(id);
      }
    }
  },
  mounted() {
    this.fetchBrands();
  }
};
