import "core-js/modules/es.array.push.js";
export default {
  data() {
    return {
      user: '',
      password: ''
    };
  },
  methods: {
    uppercase() {
      this.user = this.user.toUpperCase();
    },
    login() {
      axios.post("api/Users/Login/" + this.user + "/" + this.password).then(respuesta => {
        return respuesta.data;
      }).then(data => {
        this.$store.dispatch("guardarToken", data.token);
        this.$router.push({
          name: "dashboard"
        });
      }).catch(err => {
        console.log(err);
      });
    }
  }
};
