import axios from 'axios';

const state = {
  users: [],
  selectedUser: null,
  loading: false,
  error: null,
};

const mutations = {
  SET_USERS(state, users) {
    state.users = users;
  },
  SET_SELECTED_USER(state, user) {
    state.selectedUser = user;
  },
  SET_LOADING(state, loading) {
    state.loading = loading;
  },
  SET_ERROR(state, error) {
    state.error = error;
  },
};

const actions = {
  async fetchUsers({ commit }) {
    commit('SET_LOADING', true);
    try {
      const response = await axios.get('api/Users/ReadUsers');
      commit('SET_USERS', response.data);
    } catch (error) {
      commit('SET_ERROR', error.message);
    } finally {
      commit('SET_LOADING', false);
    }
  },
  async fetchUser({ commit }, text) {
    commit('SET_LOADING', true);
    try {
      const response = await axios.get(`api/Users/ReadUser/${text}`);
      commit('SET_USERS', response.data);
    } catch (error) {
      commit('SET_ERROR', error.message);
    } finally {
      commit('SET_LOADING', false);
    }
  },
  async createUser({ dispatch }, user) {
    await axios.post('api/Users/CreateUser', user);
    dispatch('fetchUsers');
  },
  async updateUser({ dispatch }, userData) {
    await axios.put('api/Users/UpdateUser', userData);
    dispatch('fetchUsers');
  },
  async enabledUser({ dispatch }, id) {
    await axios.put(`api/Users/EnabledUser/${id}`);
    dispatch('fetchUsers');
  },
  async disabledUser({ dispatch }, id) {
    await axios.put(`api/Users/DisabledUser/${id}`);
    dispatch('fetchUsers');
  },
};

const getters = {
  users: (state) => state.users,
  selectedUser: (state) => state.selectedUser,
  loading: (state) => state.loading,
  error: (state) => state.error,
};

export default {
  namespaced: true,
  state,
  mutations,
  actions,
  getters,
};
