import axios from 'axios';

const state = {
  suppliers: [],
  selectedSupplier: null,
  loading: false,
  error: null,
};

const mutations = {
  SET_SUPPLIERS(state, suppliers) {
    state.suppliers = suppliers;
  },
  SET_SELECTED_SUPPLIER(state, supplier) {
    state.selectedSupplier = supplier;
  },
  SET_LOADING(state, loading) {
    state.loading = loading;
  },
  SET_ERROR(state, error) {
    state.error = error;
  },
};

const actions = {
  async fetchSuppliers({ commit, rootState }) {
    commit('SET_LOADING', true);
    try {
      const response = await axios.get('api/Suppliers/ReadSuppliers');
      commit('SET_SUPPLIERS', response.data);
    } catch (error) {
      commit('SET_ERROR', error.message);
    } finally {
      commit('SET_LOADING', false);
    }
  },
  async fetchSupplier({ commit, rootState }, text) {
    commit('SET_LOADING', true);
    try {
      const response = await axios.get(`api/Suppliers/ReadSupplier/${text}`);
      commit('SET_SUPPLIERS', response.data);
    } catch (error) {
      commit('SET_ERROR', error.message);
    } finally {
      commit('SET_LOADING', false);
    }
  },
  async selectSuppliers({ commit, rootState }) {
    commit('SET_LOADING', true);
    try {
      const response = await axios.get('api/Suppliers/SelectSuppliers');
      commit('SET_SUPPLIERS', response.data);
    } catch (error) {
      commit('SET_ERROR', error.message);
    } finally {
      commit('SET_LOADING', false);
    }
  },
  async createSupplier({ dispatch, rootState }, supplier) {
    await axios.post('api/Suppliers/CreateSupplier', supplier);
    dispatch('fetchSuppliers');
  },
  async updateSupplier({ dispatch, rootState }, supplier) {
    await axios.put('api/Suppliers/UpdateSupplier', supplier);
    dispatch('fetchSuppliers');
  },
  async enabledSupplier({ dispatch, rootState }, id) {
    await axios.put(`api/Suppliers/EnabledSupplier/${id}`);
    dispatch('fetchSuppliers');
  },
  async disabledSupplier({ dispatch, rootState }, id) {
    await axios.put(`api/Suppliers/DisabledSupplier/${id}`);
    dispatch('fetchSuppliers');
  },
};

const getters = {
  suppliers: (state) => state.suppliers,
  selectedSupplier: (state) => state.selectedSupplier,
  loading: (state) => state.loading,
  error: (state) => state.error,
};

export default {
  namespaced: true,
  state,
  mutations,
  actions,
  getters,
};
