import axios from 'axios';

const state = {
    roles: [],
    loading: false,
    error: null,
  };

  const mutations = {
    SET_ROLES(state, roles) {
      state.roles = roles;
    },
    SET_LOADING(state, loading) {
      state.loading = loading;
    },
    SET_ERROR(state, error) {
      state.error = error;
    },
  };

  const actions = {
    async fetchRoles({ commit }) {
      commit('SET_LOADING', true);
      try {
        const response = await axios.get('api/Roles/ReadRoles');
        commit('SET_ROLES', response.data);
      } catch (error) {
        commit('SET_ERROR', error.message);
      } finally {
        commit('SET_LOADING', false);
      }
    },
    async selectRoles({ commit }) {
      commit('SET_LOADING', true);
      try {
        const response = await axios.get('api/Roles/SelectRoles');
        commit('SET_ROLES', response.data);
      } catch (error) {
        commit('SET_ERROR', error.message);
      } finally {
        commit('SET_LOADING', false);
      }
    },
  };

  const getters = {
    roles: (state) => state.roles,
    loading: (state) => state.loading,
    error: (state) => state.error,
  };

  export default {
    namespaced: true,
    state,
    mutations,
    actions,
    getters,
  };
