import axios from 'axios';

export const brandApi = {
  getAll() {
    return axios.get('/Brands/ReadBrands');
  },
  getBrand() {
    return axios.get(`/Brands/ReadBrand/${id}`);
  },
  create(brand) {
    return axios.post('/Brands/CreateBrand', brand);
  },
  update(brand) {
    return axios.put(`/Brands/UpdateBrand/${brand.PK_BRAND}`, brand);
  },
/*   delete(id) {
    return axios.delete(`/brands/${id}`);
  } */
};
