export default {
  props: {
    modelValue: {
      type: Boolean,
      required: true
    },
    receipt: {
      type: Object,
      default: () => ({
        pK_RECEIPT: null,
        datE_PURCHASE: ''
      })
    }
  },
  data() {
    return {
      valid: false,
      localReceipt: {
        ...this.receipt
      },
      rules: {
        required: value => !!value || 'Este campo es requerido.'
      }
    };
  },
  watch: {
    receipt: {
      handler(newReceipt) {
        this.localReceipt = {
          ...newReceipt
        };
      },
      deep: true
    }
  },
  methods: {
    close() {
      this.isOpen = false;
    },
    async saveReceipt() {
      if (this.$refs.form.validate()) {
        try {
          await this.$store.dispatch('goodsreceipt/createReceipt', {
            ...this.localReceipt
          });
          this.$emit('saved', {
            ...this.localReceipt
          });
          this.close();
        } catch (error) {
          console.error('Error al guardar la entrada:', error);
        }
      }
    }
  }
};
