import axios from 'axios';

const state = {
  products: [],
  selectedProduct: null,
  loading: false,
  error: null,
};

const mutations = {
  SET_PRODUCTS(state, products) {
    state.products = products;
  },
  SET_SELECTED_PRODUCT(state, product) {
    state.selectedProduct = product;
  },
  SET_LOADING(state, loading) {
    state.loading = loading;
  },
  SET_ERROR(state, error) {
    state.error = error;
  },
};

const actions = {
  async fetchProducts({ commit }) {
    commit('SET_LOADING', true);
    try {
      const response = await axios.get('api/Products/ReadProducts');
      commit('SET_PRODUCTS', response.data);
    } catch (error) {
      commit('SET_ERROR', error.message);
    } finally {
      commit('SET_LOADING', false);
    }
  },
  async fetchProduct({ commit }, text) {
    commit('SET_LOADING', true);
    try {
      const response = await axios.get(`api/Products/ReadProduct/${text}`);
      commit('SET_PRODUCTS', response.data);
    } catch (error) {
      commit('SET_ERROR', error.message);
    } finally {
      commit('SET_LOADING', false);
    }
  },
  async createProduct({ dispatch }, product) {
    await axios.post('api/Products/CreateProduct', product);
    dispatch('fetchProducts');
  },
  async updateProduct({ dispatch }, product) {
    await axios.put('api/Products/UpdateProduct', product);
    dispatch('fetchProducts');
  },
  async enabledProduct({ dispatch }, id) {
    await axios.put(`api/Products/EnabledProduct/${id}`);
    dispatch('fetchProducts');
  },
  async disabledProduct({ dispatch }, id) {
    await axios.put(`api/Products/DisabledProduct/${id}`);
    dispatch('fetchProducts');
  },
};

const getters = {
  products: (state) => state.products,
  selectedProduct: (state) => state.selectedProduct,
  loading: (state) => state.loading,
  error: (state) => state.error,
};

export default {
  namespaced: true,
  state,
  mutations,
  actions,
  getters,
};
